import React, { useEffect, useState } from "react";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import { Grid } from "@material-ui/core";

import axios from "axios";
import {AppZaloPayConfig} from "../../lib/config/keys";



    const uuid = require('uuid/v1'); // npm install uuid
    const moment = require('moment');

    const CryptoJS = require('crypto-js'); // npm install crypto-js
// @ts-ignore

    const queryString = require('query-string');
    const useStyles = makeStyles((theme: Theme) =>
        createStyles({
            root: {
                padding: "3rem",
            },
            form: {
                width: "100%",
            },
            input: {
                margin: "20px 0",
                "& input:valid + fieldset": {
                    borderColor: "#008fe5",
                    borderWidth: 1,
                },
                "& input:focus + fieldset": {
                    borderColor: "#008fe5 !important",
                    borderWidth: 1,
                },
                "& input:invalid + fieldset": {
                    borderColor: "#ff6767",
                    borderWidth: 1,
                },
            },

            margin: {
                margin: theme.spacing(1),
            },

            pay: {
                padding: "16px 0",
                boxShadow: "none",
                backgroundColor: "#008fe5",
                color: "#f9f9f9",
                "&:hover": {
                    boxShadow: "none",
                    backgroundColor: "#008fe5",
                    color: "#f9f9f9",
                },
            },
            clear: {
                padding: "16px 0",
                color: "#6093ff",
                textTransform: "inherit",
            },
        })
    );

    function CreateOrder() {
        const classes = useStyles();
        const [result, setResult] = useState<string>("");
        const config = {
            app_id: "15111",
            key1: "1IViCAnn06yX4arMLDMscNRn1lIOIoW2",
            endpoint: "https://sb-openapi.zalopay.vn/v2/create"
        };

        const embed_data = '{}';
        const items = '[]';
        const transID = Math.floor(Math.random() * 1000000); //yyMMdd_xxxx
        const order = {
            app_id: AppZaloPayConfig.AppId,
            app_trans_id: `${moment().format('YYMMDD')}_${transID}`, // translation missing: vi.docs.shared.sample_code.comments.app_trans_id
            app_user: "user123",
            app_time: Date.now(), // miliseconds
            item: JSON.stringify(items),
            embed_data: JSON.stringify(embed_data),
            amount: 50000,
            description: `ZaloPay - Payment for the order #${transID}`,
            bank_code: "zalopayapp",
            mac: "",
        };

            // appid|app_trans_id|appuser|amount|apptime|embeddata|item
        const data = config.app_id + "|" + order.app_trans_id + "|" + order.app_user + "|" + order.amount + "|" + order.app_time + "|" + order.embed_data + "|" + order.item;
        order.mac = CryptoJS.HmacSHA256(data, config.key1).toString();

        axios.post(config.endpoint, null, {params: order})
           .then(res=>{
               console.log(res);
               const data = res.data;
               console.log("data:", JSON.stringify(data, null, 2));
               if (res.data) {
                   console.log("order_url: ", data['order_url']);
                   window.href = data['order_url'];
               }
               })
           .catch(err=>{
               console.log(err)
           })

        const HandlePayOrder = (e: any)=>{
            e.preventDefault();
        };

        const callback = (data: any) => {
            console.log("data callback: ", data);
            if (data.error === 1) {
                setResult("Thanh toán thành công");
            } else if (data.error === 4) {
                setResult("Người dùng huỷ việc thanh toán đơn hàng");
            } else {
                setResult(`Thanh toán đơn  hàng thất bại với mã lỗi: ${data.errorCode}`);
            }

            setTimeout(() => {
                setResult("");
            }, 5000);
        };

        return (
            <Grid
                container
                direction="column"
                justify="flex-start"
                alignItems="center"
                className={classes.root}
            >
                <form
                    noValidate
                    autoComplete="off"
                    className={classes.form}
                    onSubmit={HandlePayOrder}
                    >
                 </form>
        <p style={{ paddingTop: "20px", color: "#737373" }}>{result}</p>
            </Grid>
    );
}
export default CreateOrder;
