import React, { useEffect, useState } from "react";
import TextField from "@material-ui/core/TextField";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import { Grid } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import { useFullPaymentStore } from "./store";

// @ts-ignore
const zaloPay: any = window.ZaloPay;
const queryString = require('query-string');
const parsed = queryString.parse(window.location.search);
const apptime= Date.now();


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            padding: "3rem",
        },
        form: {
            width: "100%",
        },
        input: {
            margin: "20px 0",
            "& input:valid + fieldset": {
                borderColor: "#008fe5",
                borderWidth: 1,
            },
            "& input:focus + fieldset": {
                borderColor: "#008fe5 !important",
                borderWidth: 1,
            },
            "& input:invalid + fieldset": {
                borderColor: "#ff6767",
                borderWidth: 1,
            },
        },

        margin: {
            margin: theme.spacing(1),
        },

        pay: {
            padding: "16px 0",
            boxShadow: "none",
            backgroundColor: "#008fe5",
            color: "#f9f9f9",
            "&:hover": {
                boxShadow: "none",
                backgroundColor: "#008fe5",
                color: "#f9f9f9",
            },
        },
        clear: {
            padding: "16px 0",
            color: "#6093ff",
            textTransform: "inherit",
        },
    })
);

function OrderForm() {
    const classes = useStyles();
    const { app, setAppID, appUser, setAppUser, appTime, setAppTime, amount, setAmount, appTransID, setAppTransID, embedData, setEmbedData, item, setItem, description, setDescription, mac, setMac, subAppUser, setSubAppUser, subAppID, setsubAppID } = useFullPaymentStore();
    const [result, setResult] = useState<string>("");
    const [muid, setMUID] = useState<string>("");
    const [maccesstoken, setMaccesstoken] = useState<string>("");

    useEffect(() => {

        if (typeof parsed.appid === 'string') {
            setAppID(parsed.appid)
        }
        if (typeof parsed.appuser === 'string') {
            setAppUser(parsed.appuser)
        }
        if (typeof parsed.apptime === 'string') {
            setAppTime(parsed.apptime)
        }
        if (typeof parsed.amount === 'string') {
            setAmount(parsed.amount)
        }
        if (typeof parsed.apptransid === 'string') {
            setAppTransID(parsed.apptransid)
        }
        if (typeof parsed.embeddata === 'string') {
            setEmbedData(parsed.embeddata)
        }
        if (typeof parsed.item === 'string') {
            setItem(parsed.item)
        }
        if (typeof parsed.description === 'string') {
            setDescription(parsed.description)
        }
        if (typeof parsed.mac === 'string') {
            setMac(parsed.mac)
        }
        if (typeof parsed.subappid === 'string') {
            setsubAppID(parsed.subappid)
        }
        if (typeof parsed.subappuser === 'string') {
            setSubAppUser(parsed.subappuser)
        }
    }, [])

    const handleAppOnChange = (e: any) => {
        setAppID(e.target.value);
    };

    const handleAppUserChange = (e: any) => {
        setAppUser(e.target.value);
    };

    const handleAppTimeChange = (e: any) => {
        setAppTime(e.target.value);
    };

    const handleAmountChange = (e: any) => {
        setAmount(e.target.value);
    };

    const handleAppTransIDChange = (e: any) => {
        setAppTransID(e.target.value);
    };

    const handleEmbedDataChange = (e: any) => {
        setEmbedData(e.target.value);
    };

    const handleItemChange = (e: any) => {
        setItem(e.target.value);
    };

    const handleDescriptionChange = (e: any) => {
        setDescription(e.target.value);
    };

    const handleMacChange = (e: any) => {
        setMac(e.target.value);
    };

    const handleSubAppIDChange = (e: any) => {
        setsubAppID(e.target.value);
    };

    const handleSubAppUserChange = (e: any) => {
        setSubAppUser(e.target.value);
    };

    const handlePayOrder = (e: any) => {
        e.preventDefault();
        zaloPay.payOrder({
            appid: parseInt(app),
            appuser: appUser,
            apptime: parseInt(appTime),
            amount: parseInt(amount),
            apptransid: appTransID,
            embeddata: embedData,
            item: item,
            description: description,
            mac: mac,
            subappid: subAppID,
            subappuser: subAppUser
        }, callback);
    };

    const handleClearData = () => {
        console.log("cleared");
        setAppID("");
        setAppUser("");
        setAppTime("");
        setAppTransID("");
        setAmount("");
        setEmbedData("");
        setItem("");
        setDescription("");
        setMac("");
        setsubAppID("");
        setSubAppUser("");
    };

    const handleDeepLinkCall = () => {
        zaloPay.call("getUserInfo", { appid: app }, (userData: any) => {
            console.log("data callback: ", userData);
            if (userData?.errorCode === 1) {
                window.location.href = encodeURI(`zalopay-1://post?muid=${userData?.data?.muid}&maccesstoken=${userData?.data?.maccess_tokenstoken}&appid=${app}&apptransid=${appTransID}&appuser=${appUser}&apptime=${appTime}&item=${item}&description=${description}&embeddata=${embedData}&amount=${amount}&mac=${mac}&subappid=${subAppID}&subappuser=${subAppUser}`)
            }
        });
    }

    const logDeepLink = () => {
        zaloPay.call("getUserInfo", { appid: app }, (userData: any) => {
            console.log("data callback: ", userData);
            if (userData?.errorCode === 1) {
                setMUID(userData?.data?.muid)
                setMaccesstoken(userData?.data?.maccess_token)
                console.log("----DeeplinkCall")
                console.log(encodeURI(`zalopay-1://post?muid=${userData?.data?.muid}&maccesstoken=${userData?.data?.maccess_token}&appid=${app}&apptransid=${appTransID}&appuser=${appUser}&apptime=${appTime}&item=${item}&description=${description}&embeddata=${embedData}&amount=${amount}&mac=${mac}&subappid=${subAppID}&subappuser=${subAppUser}`))
                console.log("----MUID")
                console.log(userData?.data?.muid)
                console.log("----mAccessToken")
                console.log(userData?.data?.maccess_token)
            }
        });
    }
    const callback = (data: any) => {
        console.log("data callback: ", data);
        if (data.error === 1) {
            setResult("Thanh toán thành công");
        } else if (data.error === 4) {
            setResult("Người dùng huỷ việc thanh toán đơn hàng");
        } else {
            setResult(`Thanh toán đơn hàng thất bại với mã lỗi: ${data.errorCode}`);
        }

        setTimeout(() => {
            setResult("");
        }, 5000);
    };

    return (
        <Grid
            container
            direction="column"
            justify="flex-start"
            alignItems="center"
            className={classes.root}
        >
            <form
                noValidate
                autoComplete="off"
                className={classes.form}
                onSubmit={handlePayOrder}
            >
                <Grid item>
                    <TextField
                        id="app-input"
                        required
                        autoFocus={true}
                        type={"number"}
                        label="App"
                        value={app}
                        variant="outlined"
                        fullWidth
                        className={classes.input}
                        onChange={handleAppOnChange}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="app-user-input"
                        required
                        autoFocus={true}
                        type={"string"}
                        label="AppUser"
                        value={appUser}
                        variant="outlined"
                        fullWidth
                        className={classes.input}
                        onChange={handleAppUserChange}
                    />
                </Grid>
                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="AppTime"
                        value={appTime}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleAppTimeChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="Amount"
                        value={amount}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleAmountChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="AppTransID"
                        value={appTransID}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleAppTransIDChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="EmbedData"
                        value={embedData}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleEmbedDataChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="Item"
                        value={item}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleItemChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="Description"
                        value={description}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleDescriptionChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="Mac"
                        value={mac}
                        variant="outlined"
                        placeholder={"token"}
                        fullWidth
                        className={classes.input}
                        onChange={handleMacChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="SubAppID"
                        value={subAppID}
                        variant="outlined"
                        placeholder={"SubAppID"}
                        fullWidth
                        className={classes.input}
                        onChange={handleSubAppIDChange}
                    />
                </Grid>

                <Grid item>
                    <TextField
                        id="token-input"
                        required
                        label="SubAppUser"
                        value={subAppUser}
                        variant="outlined"
                        placeholder={"SubAppUser"}
                        fullWidth
                        className={classes.input}
                        onChange={handleSubAppUserChange}
                    />
                </Grid>

                <Grid item>
                    <Button
                        variant={"text"}
                        size={"medium"}
                        fullWidth
                        className={classes.clear}
                        onClick={handleClearData}
                    >
                        Xoá data
            </Button>
                </Grid>
                <Grid item>
                    <Button
                        variant={"text"}
                        size={"medium"}
                        style={{ marginTop: "5px", marginBottom: "5px" }}
                        fullWidth
                        className={classes.clear}
                        onClick={logDeepLink}
                    >
                        LogDeepLink
            </Button>
                </Grid>

                <Grid item>
                    <Button
                        variant={"contained"}
                        size={"medium"}
                        fullWidth
                        className={classes.pay}
                        style={{ backgroundColor: "#fc820d" }}
                        type={"submit"}
                        disabled={
                            !app || !appUser || !appTime || !amount || !appTransID || !description || !mac
                        }
                    >
                        Thanh Toán
            </Button>
                </Grid>
                <Grid item>
                    <Button
                        variant={"contained"}
                        size={"medium"}
                        fullWidth
                        className={classes.pay}
                        style={{ backgroundColor: "#42a1f5", marginTop: "5px" }}
                        onClick={handleDeepLinkCall}
                    >
                        CallDeepLink
            </Button>
                </Grid>
            </form>
            <p style={{ paddingTop: "20px", color: "#737373" }}>{result}</p>
        </Grid>
    );
}

export default OrderForm;
