import React, { useEffect, useState } from "react";
import TextField from "@material-ui/core/TextField";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import { Grid } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import OrderForm from "../full-payment/order-form";


const uuid = require('uuid/v1'); // npm install uuid
const moment = require('moment');

const CryptoJS = require('crypto-js'); // npm install crypto-js
// @ts-ignore
const zaloPay: any = window.ZaloPay;
const queryString = require('query-string');
const parsed = queryString.parse(window.location.search);
const apptime= Date.now();
const apptransid= `${moment().format('YYMMDD')}_${uuid()}`;
const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            padding: "3rem",
        },
        form: {
            width: "100%",
        },
        input: {
            margin: "20px 0",
            "& input:valid + fieldset": {
                borderColor: "#008fe5",
                borderWidth: 1,
            },
            "& input:focus + fieldset": {
                borderColor: "#008fe5 !important",
                borderWidth: 1,
            },
            "& input:invalid + fieldset": {
                borderColor: "#ff6767",
                borderWidth: 1,
            },
        },

        margin: {
            margin: theme.spacing(1),
        },

        pay: {
            padding: "16px 0",
            boxShadow: "none",
            backgroundColor: "#008fe5",
            color: "#f9f9f9",
            "&:hover": {
                boxShadow: "none",
                backgroundColor: "#008fe5",
                color: "#f9f9f9",
            },
        },
        clear: {
            padding: "16px 0",
            color: "#6093ff",
            textTransform: "inherit",
        },
    })
);
function CreateOrder(){
    const config = {
        appid: "553",
        key1: "9phuAOYhan4urywHTh0ndEXiV3pKHr5Q",
        key2: "Iyz2habzyr7AG8SgvoBCbKwKi3UzlLi3",
        endpoint: "https://sandbox.zalopay.com.vn/v001/tpe/createorder"
    };

    const embeddata = {
        merchantinfo: "embeddata123"
    };

    const items = [{
        itemid: "knb",
        itemname: "kim nguyen bao",
        itemprice: 198400,
        itemquantity: 1
    }];

    const order = {
        appid: config.appid,
        apptransid: `${moment().format('YYMMDD')}_${uuid()}`, // mã giao dich có định dạng yyMMdd_xxxx
        appuser: "sonnh12",
        apptime: Date.now(), // miliseconds
        item: JSON.stringify(items),
        embeddata: JSON.stringify(embeddata),
        amount: 50000,
        description: "ZaloPay Integration Demo",
        bankcode: "zalopayapp",
        mac:"",
    };

// appid|apptransid|appuser|amount|apptime|embeddata|item
    const data = config.appid + "|" + order.apptransid + "|" + order.appuser + "|" + order.amount + "|" + order.apptime + "|" + order.embeddata + "|" + order.item;
    order.mac = CryptoJS.HmacSHA256(data, config.key1).toString();

}
export default CreateOrder;
